$(document).ready(function(){Footnotes.setup()});var Footnotes={footnotetimeout:false,setup:function(){var e=$(".footnoteRef");e.unbind("mouseover",Footnotes.footnoteover);e.unbind("mouseout",Footnotes.footnoteoout);e.bind("mouseover",Footnotes.footnoteover);e.bind("mouseout",Footnotes.footnoteoout)},footnoteover:function(){clearTimeout(Footnotes.footnotetimeout);$("#footnotediv").stop();$("#footnotediv").remove();var e=$(this).attr("href").substr(1);var t=$(this).offset();var n=$(document.createElement("div"));n.attr("id","footnotediv");n.bind("mouseover",Footnotes.divover);n.bind("mouseout",Footnotes.footnoteoout);var r=document.getElementById(e);n.html("<div>"+$(r).html()+"</div>");$(document.body).append(n);var i=t.left;if(i+420>$(window).width()+$(window).scrollLeft())i=$(window).width()-420+$(window).scrollLeft();var s=t.top+10;if(s+n.height()>$(window).height()+$(window).scrollTop())s=t.top-n.height()-15;n.css({left:i,top:s,opacity:.95,position:"absolute"})},footnoteoout:function(){Footnotes.footnotetimeout=setTimeout(function(){$("#footnotediv").animate({opacity:0},600,function(){$("#footnotediv").remove()})},100)},divover:function(){clearTimeout(Footnotes.footnotetimeout);$("#footnotediv").stop();$("#footnotediv").css({opacity:.9})}}
